INSERT INTO `product` (`id_product`, `name_product`, `benefit`, `note`, `producer`, `price`, `image`, `is_deleted`, `id_animal_type`, `id_product_type`, `quantity`) VALUES
(1, '\nThức ăn cho chó Smartheart 200g', 'Thực ăn khô Smartheart có thể sử dụng trực tiếp mà không cần qua chế biến, bảo quản dễ dàng.\nSản phẩm được qua kiểm nghiệm kĩ càng.\nCác dòng sản phẩm Smartheart còn có thành phần đạm và vitamin cao tốt cho sức khỏe tim mạch, tạo nguồn dinh dưỡng dồi dào phát triển linh hoạt hơn.', 'Có thể dùng trực tiếp. Gía thành rẻ, hợp lý.', 'Smartheart', 200000, 'https://lolipet.net/wp-content/uploads/smartheart-puppy-1.5kg.jpg', 0, 1, 1, 18),
(2, '\nThức ăn cho chó con cỡ nhỏ ROYAL CANIN Mini Puppy 500g', 'ROYAL CANIN Mini Puppy có dạng viên hình tam giác phù hợp với cấu tạo răng của chó, giảm nguy cơ hình thành mảng bám răng ở chó. Kích thước hạt phù hợp cỡ răng, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó.\n\nDuy trì hệ tiêu hóa khỏe mạnh. Men tiêu hóa L.I.P hỗ trợ hấp thu chất dinh dưỡng cho chó, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Đường tự nhiên FOS: cân bằng hệ vi sinh đường ruột. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.', 'Thức ăn cho chó con cỡ nhỏ ROYAL CANIN Mini Puppy dành cho các giống chó con dưới 10 tháng tuổi. Với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó con thuộc các giống cỡ nhỏ. Thức ăn cho chó con (các giống chó cỡ nhỏ) được nghiên cứu để cung cấp dinh dưỡng theo nhu cầu thực tế của chó con.\n\nDuy trì sức đề kháng cho chó con: chất chống oxy hóa CELT. Hỗ trợ hệ tiêu hóa hoạt động ổn định: L.I.P, đường FOS. Cung cấp dinh dưỡng toàn diện cho chó: chế biến theo công thức cung cấp năng lượng cao.', 'ROYAL CANIN', 215000, 'https://www.petmart.vn/wp-content/uploads/2021/06/thuc-an-cho-cho-con-co-nho-royal-canin-mini-puppy1.jpg', 0, 1, 1, 3),
(3, '\nThức ăn cho chó Poodle con ROYAL CANIN Poodle Puppy 500g', 'Thức ăn cho chó Poodle con ROYAL CANIN Poodle Puppy với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó Poodle trong giai đoạn mới cai sữa. Sản phẩm được chế biến theo công thức hỗ trợ phát triển toàn diện, tăng sức đề kháng của chó con. Đồng thời tăng khả năng tư duy, vận động của chó con, đẩy nhanh quá trình phát triển võng mạc của chó.\n\nSản phẩm được chế biến làm mượt lông, dưỡng da cho chó Poodle. PINCH thúc đẩy sự hình thành axit amide, giúp bảo vệ da khỏi các tác nhân gây hại. OMEGA 3 và 6: kích thích tuyến bã nhờn dưới da tiết dầu.\n\nTăng sức đề kháng cho chó con. Chất chống oxy hóa giúp chó con tăng khả năng miễn dịch, giúp cho con lớn lên khỏe mạnh.\n\nHạt thức ăn hình vuông được chế biến đặc biệt giảm nguy cơ hình thành sỏi thận ở chó. Kích thước hạt phù hợp cỡ răng của chó, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó. Size hạt phù hợp cấu tạo khoang miệng của chó, thuận lợi cho tiêu hóa.\n\nThành phần bao gồm lòng trắng trứng và các vi sinh vật giúp nhuận tràng, tốt cho hệ tiêu hóa. Đặc biệt 90% protein L.I.P hỗ trợ chó hấp thu chất dinh dưỡng, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.', 'Thức ăn cho chó Poodle con ROYAL CANIN Poodle Puppy dành riêng cho tất cả các giống chó Teacup, Tiny Poodle, Toy Poodle, Standard Poodle dưới 10 tháng tuổi.', 'ROYAL CANIN', 250000, 'https://www.petmart.vn/wp-content/uploads/2021/06/thuc-an-cho-cho-poodle-con-royal-canin-poodle-puppy1-400x400.jpg', 0, 1, 1, 7),
(4, 'Thức ăn cho chó Poodle MKB All Life Stages Formula Nutrition 1kg', 'Thức ăn cho chó Poodle MKB All Life Stages Formula Nutrition giúp bổ sung tỷ lệ cân đối giữa các chất dinh dưỡng. Đảm bảo sự phát triển của cún con qua tất cả các giai đoạn phát triển. Bao gồm cả những chú chó con, chó trưởng thành, chó mang thai, chó cho con bú và chó già. Đặc biệt, loại thức ăn này sử dụng tỷ lệ dầu thấp, hạt thức ăn khô không chứa dầu mỡ. Những lợi ích khi chó Poodle sử dụng loại thức ăn này:\n\nCung cấp nguồn dinh dưỡng theo đúng tiêu chuẩn cho giống Poodle.\nHạn chế và ngăn ngừa sự chảy nước mắt gây vàng ố lông ở khóe mắt.\nPoodle cần công thức dinh dưỡng độc quyền\nCông thức chế biến ít muối và ít chất béo, hàm lượng muối được điều chỉnh khoa học.\nNgăn ngừa mảng bám răng hiệu quả với hàm lượng Canxi được điều chỉnh khoa học.\nChăm sóc và nuôi dưỡng bộ lông cho chó Poodle mềm mượt, sáng bóng và không xơ rối\nThúc đẩy tiêu hóa an toàn, thích hợp cho cả chó con.\nKhử mùi phân tối đa.', 'Thức ăn cho chó Poodle MKB All Life Stages Formula Nutrition, dành riêng cho chó Poodle, bao gồm tất cả các giai đoạn phát triển.', 'MKB', 210000, 'https://www.petmart.vn/wp-content/uploads/2020/11/thuc-an-cho-cho-poodle-mkb-all-life-stages-formula-nutrition-400x400.jpg', 0, 1, 1, 10),
(5, 'Thức ăn cho chó con hạt mềm ZENITH Puppy Chicken Potato', 'Cung cấp chất dinh dưỡng thiết yếu cho sự phát triển của cún con.\nTăng cường sức đề kháng, bảo vệ cún con khỏi rủi ro bệnh tật.\nTăng cường sức khỏe đường ruột, tốt cho hệ tiêu hóa còn non nớt.\nThành phần tươi sạch, dễ hấp thu, giảm thiểu mùi phân và mùi cơ thể.\nCho cún con một làn da khỏe mạnh và bộ lông mềm mượt.', 'Thức ăn cho chó con hạt mềm ZENITH Puppy Chicken Potato được chế biến từ thịt cừu tươi, thịt nạc gà rút xương, gạo lứt, yến mạch và dầu cá hồi. Với các thành phần tươi sạch, giàu dinh dưỡng, Zenith Puppy hạt mềm, cung cấp độ ẩm cao và lượng muối thấp, thơm ngon, dễ nhai, dễ tiêu hóa và tốt cho sức khỏe chó con.', 'ZENITH', 220000, 'https://www.petmart.vn/wp-content/uploads/2020/12/zenith-puppy-chicken-potato-400x400.jpg', 0, 1, 1, 6),
(6, 'Thức ăn hạt mềm cho chó vị thịt bò JERHIGH Meat as Meals Beef Recipe', 'JERHIGH Meat As Meals Beef Recipe mang lại cho thú cưng sự hưng phấn và ăn ngon hơn mỗi ngày. Các thành phần nguyên liệu được sản xuất dựa trên công thức đặc biệt. Giúp cân bằng dinh dưỡng cho vật nuôi. Bổ sung protein chất lượng cao duy trì năng lượng mỗi ngày. Omega 3 và 6 trong cá và các loại thảo mộc giúp bổ sung chất dinh dưỡng cho da và lông.\n\nSản phẩm bao gồm dưỡng chất Glucosamine và Chondroitin được chiết xuất từ sụn cá biển giúp bảo vệ các khớp và xương chắc khỏe. Chất xơ Inulin là một chất lợi khuẩn khuẩn tự nhiên được chiết xuất từ rễ rau diếp xoăn giúp tăng cường hệ tiêu hóa. Khoáng chất Limonite được tìm thấy ở phía Nam Nhật Bản giúp giảm bớt mùi hôi trong phân và nước tiểu. Chất Beta Glucan chiết xuất từ men vi sinh giúp tăng cường hệ miễn dịch.\n\nThức ăn cho chó JERHIGH Meat As Meals Beef Recipe được bảo quản sản phẩm ở nhiệt độ thường, tránh những nơi có độ ẩm cao. Bạn có thể xây dựng khẩu phần ăn của cún cưng dựa trên những thông số về tuổi tác và cân nặng. Để mang lại hiệu quả tốt nhất hãy cung cấp đầy đủ nước uống cho thú cưng sau mỗi bữa ăn.', 'Thức ăn hạt mềm cho chó vị thịt bò JERHIGH Meat as Meals Beef Recipe dành cho tất cả các giống chó từ bé đến trưởng thành.', 'JERHIGH', 275000, 'https://www.petmart.vn/wp-content/uploads/2016/04/thuc-an-cho-cho-hat-mem-vi-thit-bo-jerhigh-meat-as-meals-beef-recipe1-400x400.jpg', 0, 1, 1, 20),
(7, 'Thức ăn cho chó trưởng thành cỡ nhỏ ROYAL CANIN Mini Adult 1kg', 'Thức ăn cho chó ROYAL CANIN Mini Adult là dạng viên có hình dạng phù hợp với cấu tạo răng của chó, giảm nguy cơ hình thành mảng bám răng ở chó. Kích thước hạt phù hợp cỡ răng của chó, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó.\n\nDuy trì hệ tiêu hóa khỏe mạnh. Men tiêu hóa L.I.P hỗ trợ hấp thu chất dinh dưỡng cho chó, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Đường tự nhiên FOS: cân bằng hệ vi sinh đường ruột. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.', 'Thức ăn cho chó ROYAL CANIN Mini Adult từ 10 tháng tuổi trở lên. Với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó trưởng thành thuộc các giống cỡ nhỏ.\n\nROYAL CANIN Mini Adult được nghiên cứu để cung cấp dinh dưỡng theo nhu cầu thực tế của chó. Cung cấp năng lượng theo nhu cầu dinh dưỡng của chó. Duy trì da và lông khỏe mạnh: EPA, DHA. Kiểm soát cân nặng lý tưởng cho chó: L-Carnitine', 'Royal Canin', 180000, 'https://www.petmart.vn/wp-content/uploads/2021/06/thuc-an-cho-cho-truong-thanh-co-nho-royal-canin-mini-adult1.jpg', 0, 1, 1, 22),
(8, 'Thức ăn cho chó trưởng thành cỡ nhỏ ROYAL CANIN Mini Adult 2KG', 'Thức ăn cho chó ROYAL CANIN Mini Adult là dạng viên có hình dạng phù hợp với cấu tạo răng của chó, giảm nguy cơ hình thành mảng bám răng ở chó. Kích thước hạt phù hợp cỡ răng của chó, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó.\n', 'Thức ăn cho chó ROYAL CANIN Mini Adult từ 10 tháng tuổi trở lên. Với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó trưởng thành thuộc các giống cỡ nhỏ.\n', 'Royal Canin', 380000, 'https://www.petmart.vn/wp-content/uploads/2021/06/thuc-an-cho-cho-truong-thanh-co-nho-royal-canin-mini-adult1.jpg', 0, 1, 1, 15),
(9, '\nThức ăn cho chó trưởng thành cỡ vừa ROYAL CANIN Medium Adult 1KG', 'Thần phần bao gồm anbumin: thành phần quan trọng cấu tạo một số cơ quan trong cơ thể. Axit béo thiết yếu: bổ sung năng lượng, bảo vệ da và lông. Khoáng chất: giảm nguy cơ mắc các bệnh về da, giúp xương khớp khỏe mạnh. Vitamin: có tác dụng chống oxy hóa, nâng cao sức đề kháng. Xenlulose: hỗ trợ tiêu hóa, thúc đẩy sự hấp thu các chất dinh dưỡng, cân bằng hệ vi sinh đường ruột. Tốt cho da và lông. Omega3: có tác dụng hỗ trợ da lông phát triển khỏe mạnh. Kích thích chó ăn ngon: thức ăn có mùi thơm hấp dẫn, kích thích chó ăn nhiều hơn.\n\nThức ăn cho chó trưởng thành cỡ vừa ROYAL CANIN Medium Adult có dạng viên phù hợp với cấu tạo răng của chó, giảm nguy cơ hình thành mảng bám răng ở chó. Kích thước hạt phù hợp cỡ răng, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó. Hạt hình tròn giúp khuyến khích chó nhai kĩ hơn, do đó làm chậm quá trình ăn, tránh bị khó tiêu.\n\nThành phần bao gồm men tiêu hóa, albumin và probiotic giúp nhuận tràng, tốt cho hệ tiêu hóa. Men tiêu hóa L.I.P hỗ trợ hấp thu chất dinh dưỡng cho chó, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.', 'Thức ăn cho chó trưởng thành cỡ vừa ROYAL CANIN Medium Adult dành cho những chú chó trưởng thành 12 tháng tuổi trở lên. Với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó trưởng thành.', 'Royal Canin', 160000, 'https://www.petmart.vn/wp-content/uploads/2021/06/thuc-an-cho-cho-truong-thanh-royal-canin-medium-adult1-400x400.jpg', 0, 1, 1, 2),
(10, 'Thức ăn cho chó lớn hạt mềm ZENITH Adult Lamb Potato', 'Dành cho mọi giống chó trên 1 tuổi\nGiúp xương khớp chắc khoẻ, phòng tránh bệnh loãng xương\nKhông chứa ngũ cốc, không gây dị ứng\nĐược chế biến từ thịt cừu, bột gà, gạo lứt, yến mạch\nGiúp giảm bớt mùi phân và mùi cơ thể\nGiúp tăng cường sức đề kháng, phòng tránh bệnh tật\nDuy trì cơ thể cân đối, đốt cháy lượng mỡ dư thừa, cho các chú chó vóc dáng đẹp', 'Thức ăn cho chó lớn hạt mềm ZENITH Adult Lamb Potato được chế biến từ thịt cừu tươi, thịt nạc gà rút xương, gạo lứt, yến mạch và dầu cá hồi. Với các thành phần tươi sạch, giàu dinh dưỡng, hạt mềm Zenith Adult cung cấp độ ẩm cao và lượng muối thấp, thơm ngon, dễ nhai, dễ tiêu hóa và tốt cho sức khỏe chó trưởng thành.', 'IRIS OHYAMA', 220000, 'https://www.petmart.vn/wp-content/uploads/2020/12/zenith-adult-lamb-potato-400x400.jpg', 0, 1, 1, 20),
(11, 'Pate cho chó vị thịt bò IRIS OHYAMA One Care Beef\nPate cho chó vị thịt bò IRIS OHYAMA One Care Beef', 'Đặc biệt với công thức chế biến riêng tạo nên khẩu phần ăn ngon miệng cho thú cưng.\nBổ sung Protein đảm bảo một sức khỏe toàn diện hơn.\nTạo nên cảm giác thèm ăn cho cún cưng.\nPate cho chó vị thịt bò IRIS One Care Beef làm hài lòng cả những chú chó kén ăn nhất.', 'Pate cho chó vị thịt bò IRIS OHYAMA One Care Beef là sản phẩm dành cho tất cả giống chó. Với thành phần hoàn toàn từ tự nhiên và thịt bò.', 'IRIS OHYAMA', 35000, 'https://www.petmart.vn/wp-content/uploads/2019/04/pate-cho-cho-vi-thit-bo-iris-one-care-beef100g-400x400.jpg', 0, 1, 2, 15),
(12, 'Pate cho chó nước sốt vị thịt bò PEDIGREE Pouch Beef', 'Sản phẩm cũng được coi là thức ăn phụ bổ sung dinh dưỡng cho thú cưng\nCung cấp nguồn dinh dưỡng tối đa giúp cho thú cưng của bạn luôn khỏe mạnh\nĐồng thời, kích thích sự ăn ngon. Mang lại cảm giác thèm ăn cho cún cưng. Phù hợp với cả những chú chó biếng ăn nhất\nTăng cường khả năng miễn dịch\nThúc đẩy phát triển răng\nNâng cao khả năng tiêu hóa\nThúc đẩy sự phát triển xương\nThúc đẩy da và lông chắc khỏe, mềm mượt hơn', 'Pate cho chó nước sốt vị thịt bò PEDIGREE Pouch Beef với hương vị kích thích dành cho cún biếng ăn, có thể trộn với cơm, hạt khô để tạo mùi cho thức ăn. Ngoài ra trong trường hợp không có thức ăn sẵn cho vật nuôi, có thể dùng để cho cún ăn trực tiếp.', ' Pedigree', 35000, 'https://www.petmart.vn/wp-content/uploads/2012/08/pate-cho-cho-nuoc-sot-vi-thit-bo-pedigree-pouch-beef-400x400.jpg', 0, 1, 2, 16),
(13, 'Pate cho chó hỗ trợ chức năng gan và sáng mắt IRIS OHYAMA Benefit For Eyes & Liver', 'Đặc biệt với công thức chế biến riêng hỗ trợ rất tốt cho mắt.\nCải thiện được chức năng gan cho thú cưng.\nTạo nên một sức khỏe toàn diện hơn.\nHỗ trợ chức năng gan.\nDuy trì đôi mắt sáng rõ.', 'Pate cho chó hỗ trợ chức năng gan và sáng mắt IRIS OHYAMA Benefit For Eyes & Liver là sản phẩm dành cho tất cả giống chó. Với thành phần hoàn toàn từ tự nhiên.', 'IRIS OHYAMA', 35000, 'https://www.petmart.vn/wp-content/uploads/2019/04/pate-cho-cho-ho-tro-chuc-nang-gan-va-sang-mat-iris-benefit-for-eyes-liver-400x400.jpg', 0, 1, 2, 14),
(14, 'Pate cho chó vị thịt bò trộn rau củ IRIS OHYAMA Chicken Beef Vegetable', 'Đặc biệt với công thức chế biến riêng tạo nên khẩu phần ăn ngon miệng cho thú cưng\nBổ sung protein đảm bảo một sức khỏe toàn diện hơn\nIRIS Chicken Beef Vegetable có chất xơ hỗ trợ hệ tiêu hóa cực tốt', 'Pate cho chó vị thịt bò trộn rau củ IRIS OHYAMA Chicken Beef Vegetable là sản phẩm dành cho tất cả giống chó. Sản phẩm sẽ làm hài lòng những chú chó kén ăn nhất.', 'IRIS OHYAMA', 35000, 'https://www.petmart.vn/wp-content/uploads/2019/04/pate-cho-cho-vi-thit-bo-tron-rau-cu-iris-chicken-beef-vegetable-400x400.jpg', 0, 1, 2, 3),
(15, 'Pate cho chó vị bò gà nướng sốt cà rốt JERHIGH Chicken Beef Grilled Chunks Carrot In Gravy', 'Sản phẩm được làm từ thịt bò thật, kết hợp với cà rốt, được tạo thành những miếng thịt ngon và được trang trí bởi sốt ngon tuyệt vời.\nCó thể được cho chó ăn độc lập hoặc trộn với thức ăn khô để mở rộng khẩu vị và tăng cường sự thèm ăn của chó.\nĐược nghiên cứu và kiểm tra kỹ lưỡng để đảm bảo vệ sinh, an toàn và đầy đủ dinh dưỡng cho chó.', 'Pate cho chó vị bò gà nướng sốt cà rốt JERHIGH Chicken Beef Grilled Chunks Carrot In Gravy với thiết kế bao bì hoàn toàn mới. Sản phẩm cung cấp đầy đủ protein và giá trị dinh dưỡng với lượng Omega 3 và kẽm được thêm vào để giúp giữ gìn sức khỏe của da và lông của chó. Đồng thời giúp giảm các dấu hiệu lão hóa.', 'JERHIGH', 35000, 'https://www.petmart.vn/wp-content/uploads/2016/04/pate-cho-cho-vi-bo-ga-nuong-sot-ca-rot-jerhigh-chicken-beef-grilled-chunks-carrot-in-gravy-400x400.jpg', 0, 1, 2, 10),
(16, '\nPate cho chó vị bò gà nướng sốt cà rốt JERHIGH Chicken Beef Grilled Chunks Carrot In Gravy', 'Sản phẩm được bổ sung thêm chất inulin và chất xơ giúp tăng cường hệ tiêu hóa và đường ruột cho chó.\nCó thể được cho chó ăn độc lập hoặc trộn với thức ăn khô để mở rộng khẩu vị và tăng cường sự thèm ăn của chó.\nĐược nghiên cứu và kiểm tra kỹ lưỡng để đảm bảo vệ sinh, an toàn và đầy đủ dinh dưỡng cho chó.', 'Pate cho chó vị gà viên sốt rau củ JERHIGH Chicken Chunks Vegetable In Gravy với thiết kế bao bì hoàn toàn mới. Với thành phần chính là thịt gà và rau củ thơm ngon được cắt thành những miếng thịt to và thấm đượm trong nước sốt hảo hạng.', 'JERHIGH', 35000, 'https://www.petmart.vn/wp-content/uploads/2016/04/pate-cho-cho-vi-ga-vien-sot-rau-cu-jerhigh-chicken-chunks-vegetable-in-gravy-400x400.jpg', 0, 1, 2, 2),
(17, 'Pate cho chó giảm rụng lông IRIS OHYAMA Benefit For Fur', 'Đặc biệt với công thức chế biến riêng tạo nên khẩu phần ăn ngon miệng cho thú cưng.\nChăm sóc tốt cho bộ lông của cún cưng trở nên mềm mượt hơn.\nPate cho chó giảm rụng lông IRIS Benefit For Fur đảm bảo một sức khỏe toàn diện hơn.\nGiảm rụng lông ở thú cưng.\nGiúp duy trì da và lông khỏe mạnh.', 'Pate cho chó giảm rụng lông IRIS OHYAMA Benefit For Fur là sản phẩm dành cho tất cả giống chó. Với thành phần hoàn toàn từ tự nhiên.', 'IRIS OHYAMA', 35000, 'https://www.petmart.vn/wp-content/uploads/2019/04/pate-cho-cho-giam-rung-long-iris-benefit-for-fur.jpg', 0, 1, 2, 18),
(18, 'Pate cho chó vị thịt bò trộn cơm IRIS OHYAMA Beef Rice', 'Đặc biệt với công thức chế biến riêng tạo nên khẩu phần ăn ngon miệng cho thú cưng.\nBổ sung protein đảm bảo một sức khỏe toàn diện hơn.\nTinh bột từ cơm đảm bảo năng lượng hoạt động cho cả ngày của cún cưng.\nIRIS Beef Rice tạo nên cảm giác thèm ăn cho thú cưng', 'Pate cho chó vị thịt bò trộn cơm IRIS OHYAMA Beef Rice là sản phẩm dành cho tất cả giống chó. Với thành phần hoàn toàn từ tự nhiên thịt bò và cơm', 'IRIS OHYAMA', 35000, 'https://www.petmart.vn/wp-content/uploads/2019/04/pate-cho-cho-vi-thit-bo-tron-com-iris-beef-rice-400x400.jpg', 0, 1, 2, 4),
(19, '\nPate cho chó vị gan gà hầm nước sốt JERHIGH Chicken Liver Chunks In Gravy', 'Sản phẩm được làm từ thịt gà và gan thật, được cắt thành những miếng thịt ngon miệng và phủ nước sốt đậm đà.\nCó thể được cho chó ăn độc lập hoặc trộn với thức ăn khô để mở rộng khẩu vị và tăng cường sự thèm ăn của chó.\nĐược nghiên cứu và kiểm tra kỹ lưỡng để đảm bảo vệ sinh, an toàn và đầy đủ dinh dưỡng cho chó.', 'Pate cho chó vị gan gà hầm nước sốt JERHIGH Chicken Liver Chunks In Gravy với thiết kế bao bì hoàn toàn mới. Sản phẩm giàu giá trị dinh dưỡng từ vitamin E giúp duy trì sự nguyên vẹn của da và lông. Vị ngon tuyệt vời khiến chó của bạn không thể cưỡng lại được.', 'JERHIGH', 35000, 'https://www.petmart.vn/wp-content/uploads/2016/04/pate-cho-cho-vi-gan-ga-ham-nuoc-sot-jerhigh-chicken-liver-chunks-in-gravy-400x400.jpg', 0, 1, 2, 6),
(20, 'Pate cho chó vị thịt gà nướng hầm nước sốt JERHIGH Grilled Chicken Chunks In Gravy', 'Sản phẩm được làm từ thịt gà thật, được tạo thành từ những miếng thịt ngon nhất và bổ sung với nước sốt thơm phức.\nCó thể được cho chó ăn độc lập hoặc trộn với thức ăn khô để mở rộng khẩu vị và tăng cường sự thèm ăn của chó.\nĐược nghiên cứu và kiểm tra kỹ lưỡng để đảm bảo vệ sinh, an toàn và đầy đủ dinh dưỡng cho chó.', 'Pate cho chó vị thịt gà nướng hầm nước sốt JERHIGH Grilled Chicken Chunks In Gravy với thiết kế bao bì hoàn toàn mới. Đây là một loại thức ăn cao cấp dành cho chó với giá trị dinh dưỡng cao từ canxi đến vitamin D giúp cho xương và răng chó trở nên vững chắc cùng với việc tăng cường khối lượng cơ bắp.', 'JERHIGH', 35000, 'https://www.petmart.vn/wp-content/uploads/2016/04/pate-cho-cho-vi-thit-ga-nuong-jerhigh-grilled-chicken.jpg', 0, 1, 2, 14),
(22, 'Sốt pate cho chó vị gan và ức gà INABA Chicken Breast & Liver', 'Polyphenol trong trà xanh có tác dụng đặc biệt khử mùi, giảm thiểu mùi hôi trong phân thải và nước tiểu của chó.\n\nSản phẩm không chứa chất bảo quản. Sử dụng cho chó ăn trực tiếp hoặc trộn với bữa cơm hàng ngày. Đóng kín và cho vào tủ lạnh sau khi bóc hộp. Nên hâm ấm trước khi sử dụng.', 'Sốt pate cho chó vị gan và ức gà INABA Chicken Breast & Liver được sản xuất theo công nghệ hiện đại của Nhật Bản với hương vị thơm ngon cùng thành phần chủ yếu là ức gà, gan gà, rau hoặc thịt bò bổ sung thêm dinh dưỡng tốt nhất cho cún cưng của bạn.', 'INABA', 35000, 'https://www.petmart.vn/wp-content/uploads/2021/05/sot-pate-cho-cho-vi-gan-va-uc-ga-inaba-chicken-breast-liver-400x400.jpg', 0, 1, 2, 8),
(23, 'Pate cho chó vị gà, rau củ và cá ngừ INABA Chicken, Vegetables & Tuna', 'Thịt ức gà 11%, gan gà, cá ngừ 3%, rau xanh, cà rốt, bí đỏ, đậu hà lan, tinh bột, chiết xuất men gà, collagen cá, men gạo đỏ… cùng các thành phần phụ gia khác. Với thành phần chiết xuất trà xanh giúp làm giảm mùi hôi phân và nước tiểu của chó.', 'Pate cho chó vị gà, rau củ và cá ngừ INABA Chicken, Vegetables & Tuna dạng hộp đôi. Sản phẩm thức ăn ướt phù hợp với nhu cầu dinh dưỡng dành cho chó có thể tự ăn riêng hoặc trộn cùng với bữa ăn hàng ngày. Không chứa chất bảo quản.', 'INABA', 35000, 'https://www.petmart.vn/wp-content/uploads/2020/12/pate-cho-cho-vi-thit-bo-iris-hrb375-beef3.jpg', 0, 1, 2, 8),
